import React from "react";
import { useState } from "react";


function CalculateFee(){
    const [feePercentage, setFeePercentage] = useState()
    const [expectedBill, setExpectedBill] = useState()
    const totalFee = (Number(expectedBill) * Number(feePercentage/100))
    let risk
    let feeCalc

    if (totalFee === true){
        feeCalc= 
        <p>Expected Placement is valued at ~{totalFee}ï¿¥</p>
        }



    if (totalFee < 2000001){
        risk = 0
    }else if(totalFee >= 2000001 && totalFee < 3000000){
        risk = 5
    }else if(totalFee >= 3000001){
        risk = 10
    }

    return(
        <>
                    console.log(risk)

            <div className="expectedFee">
                <label htmlFor={feePercentage}>Fee Percentage : </label>
                <input 
                id={feePercentage} 
                type="number"
                onChange={event=>setFeePercentage(event.target.value)}
                >
                </input>
            </div>
            <div className="expectedFee">
                <label htmlFor={expectedBill}>Expected Billable Revenue : </label>
                <input 
                id={expectedBill} 
                type="number"
                onChange={event=>setExpectedBill(event.target.value)}
                >
                </input>
            </div>    
            {feeCalc}
        </>

    );
}


export default CalculateFee;
